mapper.select:
SELECT count(p.post_id) FROM organization.post_ p LEFT JOIN organization.employee_ e on e.employee_id=p.post_principal_id LEFT JOIN organization.company_ y on p.company_id=y.company_id LEFT JOIN organization.department_ d on d.department_id=p.department_id WHERE p.company_id=#{companyId}
AND p.post_name like '%${postName}%'
AND d.department_name like '%${departmentName}%'

mapper.update:

 update organization.post_ set is_delete='1'
 where 
 post_id = #{postId,jdbcType=VARCHAR}
