mapper.select:
select DISTINCT t.transfer_job_id,t.employee_id,e.employee_name,e.employee_no,t.department_id,(select d.department_name from department_ d where d.department_id=t.department_id), t.direct_person_id,y.employee_name AS directPersonName,t.transfer_job_cause, t.transfer_begin_time,t.transfer_end_time,t.user_id,t.operating_time, e.login_name, e.employee_sex, e.employee_phone, e.employee_twophone,e.entry_time,e.company_id,c.company_name, e.employee_status,e.probationary_expired,m.post_id,s.post_name,m.post_grades,m.is_delete,m.post_staus
from transfer_job_ t LEFT JOIN employee_ e on e.employee_id=t.employee_id lEFT JOIN employee_ y on y.employee_id=t.direct_person_id lEFT JOIN department_ d on d.department_id=t.department_id lEFT JOIN company_ c on c.company_id=e.company_id LEFT JOIN connect_emp_post_ m on m.employee_id=t.employee_id and m.department_id=t.department_id and m.post_grades='1' LEFT JOIN post_ s on s.post_id=m.post_id
and t.company_id=#{companyId}
and t.employee_id=#{employeeId}
and #{positionTime} between t.transfer_begin_time and t.transfer_end_time

select DISTINCT t.transfer_job_id,t.employee_id,e.employee_name,e.employee_no,t.department_id,(select d.department_name from department_ d where d.department_id=t.department_id), t.direct_person_id,y.employee_name AS directPersonName,t.transfer_job_cause, t.transfer_begin_time,t.transfer_end_time,t.user_id,t.operating_time, e.login_name, e.employee_sex, e.employee_phone, e.employee_twophone,e.entry_time,e.company_id,c.company_name, e.employee_status,e.probationary_expired,m.post_id,s.post_name,m.post_grades,m.is_delete,m.post_staus
from transfer_job_ t LEFT JOIN employee_ e on e.employee_id=t.employee_id lEFT JOIN employee_ y on y.employee_id=t.direct_person_id lEFT JOIN department_ d on d.department_id=t.department_id lEFT JOIN company_ c on c.company_id=e.company_id LEFT JOIN connect_emp_post_ m on m.employee_id=t.employee_id and m.department_id=t.department_id and m.post_grades='1' LEFT JOIN post_ s on s.post_id=m.post_id
and t.company_id=#{companyId}
and t.employee_id =#{employeeId}
and t.transfer_end_time is null
